import React, { useState, useEffect } from "react";
import { Layout, InputNumber, Button, Row, Col, Typography, message, Switch, Radio, Select, Spin } from "antd";
import PriceTable from "./components/PriceTable";
import { fetchRandomSample, fetchTopTokens, fetchPriceRanges } from "./api";

const { Header, Content } = Layout;
const { Title } = Typography;

function App() {
  const [rows, setRows] = useState(100);
  const [tokens, setTokens] = useState(10);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState([]);
  const [timezone, setTimezone] = useState("gmt8");
  const [showFullToken, setShowFullToken] = useState(false);
  const [priceUnit, setPriceUnit] = useState("USD"); // 默认USD
  const [topTokens, setTopTokens] = useState([]);
  const [selectedToken, setSelectedToken] = useState(undefined);
  const [tokenLoading, setTokenLoading] = useState(false);
  const [priceType, setPriceType] = useState("buy_price"); // buy_price/sell_price
  const [priceRanges, setPriceRanges] = useState({ low: null, mid: null, high: null });
  const [selectedRange, setSelectedRange] = useState(undefined);
  const [rangeLoading, setRangeLoading] = useState(false);

  useEffect(() => {
    setTokenLoading(true);
    fetchTopTokens(20)
      .then(list => setTopTokens(list))
      .catch(() => message.error("高频token列表获取失败"))
      .finally(() => setTokenLoading(false));
  }, []);

  useEffect(() => {
    setRangeLoading(true);
    fetchPriceRanges(priceType)
      .then(ranges => setPriceRanges(ranges))
      .catch(() => message.error("价格区间获取失败"))
      .finally(() => setRangeLoading(false));
  }, [priceType]);

  const handleQuery = async () => {
    setLoading(true);
    try {
      const result = await fetchRandomSample(rows, tokens);
      setData(result);
    } catch (e) {
      message.error("查询失败，可能是token数量超出范围或后端错误");
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  // token筛选
  let filteredData = data;
  if (selectedToken) {
    filteredData = filteredData.filter(row => row.token_mint_address === selectedToken);
  }
  // 价格区间筛选
  if (selectedRange && priceRanges.low !== null && priceRanges.mid !== null && priceRanges.high !== null) {
    filteredData = filteredData.filter(row => {
      const price = priceType === "buy_price" ? row.buy_price : row.sell_price;
      if (price === null || price === undefined || price <= 0) return false;
      if (selectedRange === "low") return price <= priceRanges.low;
      if (selectedRange === "mid") return price > priceRanges.low && price <= priceRanges.mid;
      if (selectedRange === "high") return price > priceRanges.mid;
      return true;
    });
  }

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ background: "#fff", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <Title level={3} style={{ margin: 0 }}>简易 Price 比较器</Title>
        <div style={{ display: 'flex', alignItems: 'center', gap: 24 }}>
          <span>高频Token筛选：</span>
          <Spin spinning={tokenLoading} size="small">
            <Select
              allowClear
              showSearch
              style={{ minWidth: 200 }}
              placeholder="请选择token"
              value={selectedToken}
              onChange={setSelectedToken}
              optionFilterProp="children"
              filterOption={(input, option) =>
                (option?.children ?? '').toLowerCase().includes(input.toLowerCase())
              }
            >
              {topTokens.map(t => (
                <Select.Option key={t.token_mint_address} value={t.token_mint_address}>
                  {t.token_mint_address.slice(0, 8)}... ({t.count})
                </Select.Option>
              ))}
            </Select>
          </Spin>
          <span>价格类型：</span>
          <Radio.Group
            value={priceType}
            onChange={e => setPriceType(e.target.value)}
            style={{ marginRight: 24 }}
          >
            <Radio.Button value="buy_price">买价</Radio.Button>
            <Radio.Button value="sell_price">卖价</Radio.Button>
          </Radio.Group>
          <span>价格区间：</span>
          <Spin spinning={rangeLoading} size="small">
            <Select
              allowClear
              style={{ minWidth: 120 }}
              placeholder="请选择区间"
              value={selectedRange}
              onChange={setSelectedRange}
            >
              <Select.Option value="low">低价（≤{priceRanges.low ?? "-"}）</Select.Option>
              <Select.Option value="mid">中价（{priceRanges.low ?? "-"}~{priceRanges.mid ?? "-"}）</Select.Option>
              <Select.Option value="high">高价（>{priceRanges.mid ?? "-"}）</Select.Option>
            </Select>
          </Spin>
          <span>显示价格单位：</span>
          <Radio.Group
            value={priceUnit}
            onChange={e => setPriceUnit(e.target.value)}
            style={{ marginRight: 24 }}
          >
            <Radio.Button value="SOL">SOL</Radio.Button>
            <Radio.Button value="USD">USD</Radio.Button>
          </Radio.Group>
          <span>显示时间：</span>
          <Switch
            checkedChildren="GMT+8"
            unCheckedChildren="GMT+0"
            checked={timezone === "gmt8"}
            onChange={checked => setTimezone(checked ? "gmt8" : "gmt0")}
            style={{ marginRight: 24 }}
          />
          <span>Token地址全显</span>
          <Switch
            checked={showFullToken}
            onChange={setShowFullToken}
          />
        </div>
      </Header>
      <Content style={{ padding: 24 }}>
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col>
            <span>拉取rows数量：</span>
            <InputNumber min={1} max={10000} value={rows} onChange={setRows} />
          </Col>
          <Col>
            <span>不同token数量：</span>
            <InputNumber min={1} max={1000} value={tokens} onChange={setTokens} />
          </Col>
          <Col>
            <Button type="primary" onClick={handleQuery} loading={loading}>
              查询
            </Button>
          </Col>
        </Row>
        <PriceTable
          data={filteredData}
          loading={loading}
          timezone={timezone}
          showFullToken={showFullToken}
          priceUnit={priceUnit}
        />
      </Content>
    </Layout>
  );
}

export default App;
