import React, { useState, useEffect } from "react";
import { Layout, InputNumber, Button, Row, Col, Typography, message, Switch, Radio, Select, Spin, Space } from "antd"; 
import PriceTable from "./components/PriceTable";
import { fetchRandomSample, fetchTopTokens, fetchPriceRanges } from "./api";

const { Header, Content } = Layout;
const { Title } = Typography;
const { Option } = Select; 

function App() {
  const [rows, setRows] = useState(100);
  const [tokens, setTokens] = useState(10);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filteredData, setFilteredData] = useState([]);
  const [topTokens, setTopTokens] = useState([]);
  const [tokenLoading, setTokenLoading] = useState(false);
  const [selectedToken, setSelectedToken] = useState(null);
  const [priceType, setPriceType] = useState("buy_price");
  const [priceRanges, setPriceRanges] = useState({ low: null, mid: null, high: null });
  const [rangeLoading, setRangeLoading] = useState(false);
  const [selectedRange, setSelectedRange] = useState(null);
  const [priceUnit, setPriceUnit] = useState("SOL");
  const [timezone, setTimezone] = useState("gmt8"); 
  const [showFullToken, setShowFullToken] = useState(false);

  useEffect(() => {
    setTokenLoading(true);
    fetchTopTokens(20)
      .then(list => setTopTokens(list))
      .catch(() => message.error("高频token列表获取失败"))
      .finally(() => setTokenLoading(false));
  }, []);

  useEffect(() => {
    setRangeLoading(true);
    fetchPriceRanges(priceType)
      .then(ranges => setPriceRanges(ranges))
      .catch(() => message.error("价格区间获取失败"))
      .finally(() => setRangeLoading(false));
  }, [priceType]);

  useEffect(() => {
    let intermediateData = data;

    // Token filter
    if (selectedToken) {
      intermediateData = intermediateData.filter(row => row.token_mint_address === selectedToken);
    }

    // Price range filter
    if (selectedRange && priceRanges.low !== null && priceRanges.mid !== null && priceRanges.high !== null) {
      intermediateData = intermediateData.filter(row => {
        const price = priceType === "buy_price" ? row.buy_price : row.sell_price;
        if (selectedRange === "low") return price <= priceRanges.low;
        if (selectedRange === "mid") return price > priceRanges.low && price <= priceRanges.mid;
        if (selectedRange === "high") return price > priceRanges.mid;
        return true;
      });
    }
    setFilteredData(intermediateData);
  }, [data, selectedToken, selectedRange, priceType, priceRanges]);

  const handleQuery = () => {
    setLoading(true);
    fetchRandomSample(rows, tokens)
      .then(res => {
        setData(res.data || []);
        message.success(`成功获取 ${res.data?.length || 0} 条数据`);
      })
      .catch(err => {
        console.error("Fetch error:", err);
        message.error("获取数据失败");
        setData([]);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ background: "#fff", padding: "12px 24px", height: 'auto' }}>
        <Title level={4} style={{ margin: 0, marginBottom: 8, float: 'left', marginRight: 16 }}>Price Checker</Title>
        <Row gutter={[8, 8]} align="middle" wrap>
          <Col>
            <Spin spinning={tokenLoading} size="small">
              <Select
                allowClear
                showSearch
                style={{ minWidth: 140 }}
                placeholder="Token"
                value={selectedToken}
                onChange={setSelectedToken}
                optionFilterProp="children"
                filterOption={(input, option) =>
                  (option?.children ?? '').toLowerCase().includes(input.toLowerCase())
                }
              >
                {topTokens.map(t => (
                  <Option key={t.token_mint_address} value={t.token_mint_address}>
                    {t.token_mint_address.slice(0, 6)}... ({t.count})
                  </Option>
                ))}
              </Select>
            </Spin>
          </Col>
          <Col>
            <Select
              value={priceType}
              onChange={setPriceType}
              style={{ minWidth: 80 }}
            >
              <Option value="buy_price">买价</Option>
              <Option value="sell_price">卖价</Option>
            </Select>
          </Col>
          <Col>
            <Spin spinning={rangeLoading} size="small">
              <Select
                allowClear
                style={{ minWidth: 120 }}
                placeholder="价格区间"
                value={selectedRange}
                onChange={setSelectedRange}
              >
                <Option value="low">≤{priceRanges.low ?? "-"}</Option>
                <Option value="mid">({priceRanges.low ?? "-"} ~ {priceRanges.mid ?? "-"}]</Option>
                <Option value="high">&gt;{priceRanges.mid ?? "-"}</Option>
              </Select>
            </Spin>
          </Col>
          <Col>
            <InputNumber addonBefore="Rows" min={1} max={10000} value={rows} onChange={setRows} style={{ width: 130 }} />
          </Col>
          <Col>
            <InputNumber addonBefore="Tokens" min={1} max={1000} value={tokens} onChange={setTokens} style={{ width: 130 }} />
          </Col>
          <Col>
            <Select
              value={priceUnit}
              onChange={setPriceUnit}
              style={{ minWidth: 70 }}
            >
              <Option value="SOL">SOL</Option>
              <Option value="USD">USD</Option>
            </Select>
          </Col>
          <Col>
            <Select
              value={timezone}
              onChange={setTimezone}
              style={{ minWidth: 80 }}
            >
              <Option value="gmt0">GMT+0</Option>
              <Option value="gmt8">GMT+8</Option>
            </Select>
          </Col>
          <Col>
            <Select
              value={showFullToken}
              onChange={setShowFullToken} 
              style={{ minWidth: 90 }}
            >
              <Option value={false}>Token缩略</Option>
              <Option value={true}>Token全显</Option>
            </Select>
          </Col>
          <Col>
            <Button type="primary" onClick={handleQuery} loading={loading}>
              查询
            </Button>
          </Col>
        </Row>
      </Header>
      <Content style={{ padding: 24 }}>
        <PriceTable
          data={filteredData}
          loading={loading}
          timezone={timezone}
          showFullToken={showFullToken}
          priceUnit={priceUnit}
        />
      </Content>
    </Layout>
  );
}

export default App;
