import React, { useState, useEffect } from "react";
import { Layout, InputNumber, Button, Row, Col, Typography, message, Select, Spin, Tabs } from "antd";
import PriceTable from "./components/PriceTable";
import AbnormalFilterButton from "./components/AbnormalFilterButton";
import { fetchRandomSample, fetchTopTokens, fetchBatchBinsData, fetchFilterData } from "./api";

const { Header, Content } = Layout;
const { Title } = Typography;
const { Option } = Select;
const { TabPane } = Tabs;

function App() {
  const [rows, setRows] = useState(100);
  const [tokens, setTokens] = useState(10);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [topTokens, setTopTokens] = useState([]);
  const [tokenLoading, setTokenLoading] = useState(false);
  const [selectedToken, setSelectedToken] = useState(null);
  const [priceType, setPriceType] = useState("buy_price");
  const [priceUnit, setPriceUnit] = useState("USD"); 
  const [timezone, setTimezone] = useState("gmt8");
  const [showFullToken, setShowFullToken] = useState(false);
  const [solPrice, setSolPrice] = useState(133); 
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);
  const [total, setTotal] = useState(0);
  const [mode, setMode] = useState('all'); // all: 全量分页, sample: 随机抽样, abnormal: 异常模式
  const [summary, setSummary] = useState(null);
  const [samplePage, setSamplePage] = useState(1);
  const [samplePageSize, setSamplePageSize] = useState(20);
  const [abnormalMode, setAbnormalMode] = useState(false); // 新增异常模式

  // 区间tab相关
  const binIdxList = [4, 3, 2, 5, 1, 0]; // 保證 tab 與預加載順序均以 100-1K USD (bin 4) 為第一順位
  const [activeBin, setActiveBin] = useState(4); // 預設 activeBin 也為 4
  const [binData, setBinData] = useState({}); // {binIdx: {data, total}}
  const [binLoading, setBinLoading] = useState(false);

  const PAGE_BATCH_SIZE = 100; // 每次批量加载100条

  // 批量加载指定bin的指定页码区块（修正版）
  const loadBinDataBatch = async (binIdx, startPage, pageSize = 20) => {
    const batchSize = PAGE_BATCH_SIZE;
    const batchPageCount = Math.ceil(batchSize / pageSize);
    const pagesToLoad = [];
    const loadedPages = binData[binIdx]?.loadedPages || new Set();
    for (let i = 0; i < batchPageCount; i++) {
      const pageNum = startPage + i;
      if (!loadedPages.has(pageNum)) pagesToLoad.push(pageNum);
    }
    if (pagesToLoad.length === 0) return;
    setBinLoading(true);
    const results = await Promise.all(
      pagesToLoad.map(pageNum => fetchBatchBinsData({ priceType, priceUnit, solPrice, bins: [binIdx], page: pageNum, pageSize }))
    );
    setBinData(prev => {
      const prevData = prev[binIdx]?.data || [];
      const prevLoaded = prev[binIdx]?.loadedPages || new Set();
      let allData = [...prevData];
      let total = prev[binIdx]?.total;
      results.forEach((res, idx) => {
        const pageNum = pagesToLoad[idx];
        if (Array.isArray(res[binIdx]?.data)) {
          allData[(pageNum - 1) * pageSize] = undefined; // 占位
          res[binIdx].data.forEach((row, i) => {
            allData[(pageNum - 1) * pageSize + i] = row;
          });
          total = res[binIdx].total;
        }
        prevLoaded.add(pageNum);
      });
      return {
        ...prev,
        [binIdx]: {
          data: allData,
          total,
          loadedPages: new Set(prevLoaded)
        }
      };
    });
    setBinLoading(false);
  };

  useEffect(() => {
    setActiveBin(4); // 保證初次渲染時tab正確
  }, []);

  useEffect(() => {
    loadBinDataBatch(activeBin, 1, pageSize);
    // eslint-disable-next-line
  }, [activeBin, priceType, priceUnit, solPrice, pageSize]);

  useEffect(() => {
    setTokenLoading(true);
    fetchTopTokens(20)
      .then(setTopTokens)
      .catch(err => console.error("Fetch top tokens error:", err))
      .finally(() => setTokenLoading(false));
  }, []);

  useEffect(() => {
    let intermediateData = data;
    if (selectedToken) {
      intermediateData = intermediateData.filter(row => row.token_mint_address === selectedToken);
    }
    setData(intermediateData);
  }, [data, selectedToken]);

  const fetchPageData = (params = {}) => {
    setLoading(true);
    fetchFilterData({
      priceType,
      priceUnit,
      solPrice,
      token: selectedToken,
      page,
      pageSize,
      ...params
    })
      .then(res => {
        setData(res.data || []);
        setTotal(res.total || 0);
        setSummary(res.summary || null);
        setMode('all');
      })
      .catch(err => {
        message.error('获取数据失败');
        setData([]);
        setSummary(null);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    setPage(1);
    fetchPageData({ page: 1 });
  }, [selectedToken, priceType, priceUnit, solPrice]); // 移除 buyPriceFilter 依赖

  // 异常值过滤切换（全量异常）
  const handleAbnormalFilter = () => {
    setLoading(true);
    fetchFilterData({
      priceType,
      priceUnit,
      solPrice,
      token: selectedToken,
      page: 1,
      pageSize,
      abnormal_only: true
    })
      .then(res => {
        setData(res.data || []);
        setTotal(res.total || 0);
        setSummary(res.summary || null);
        setMode('abnormal'); // 新增异常模式
        setPage(1);
      })
      .catch(err => {
        message.error('获取异常数据失败');
        setData([]);
        setSummary(null);
      })
      .finally(() => setLoading(false));
  };

  // 重置按钮恢复到初始状态
  const handleReset = () => {
    setMode('all');
    setPage(1);
    setPageSize(20);
    setAbnormalMode(false);
    fetchPageData({ page: 1 }); // fetchPageData 已带当前 tab/bin/筛选条件
  };

  const handleTableChange = (pagination) => {
    setPage(pagination.current);
    setPageSize(pagination.pageSize);
    const batchSize = PAGE_BATCH_SIZE;
    const batchPageCount = Math.ceil(batchSize / pagination.pageSize);
    const batchIdx = Math.floor((pagination.current - 1) / batchPageCount);
    const batchStart = batchIdx * batchPageCount + 1;
    loadBinDataBatch(activeBin, batchStart, pagination.pageSize);
  };

  const handleSampleTableChange = (pagination) => {
    setSamplePage(pagination.current);
    setSamplePageSize(pagination.pageSize);
  };

  const handleQuery = () => {
    setLoading(true);
    fetchRandomSample(rows, tokens, { priceType, priceUnit, solPrice, token: selectedToken })
      .then(res => {
        setData(res.data || []);
        setTotal(res.total || 0);
        setSummary(res.summary || null);
        setMode('sample');
        message.success(`成功获取 ${res.data?.length || 0} 条随机样本`);
      })
      .catch(err => {
        message.error('获取数据失败');
        setData([]);
        setSummary(null);
      })
      .finally(() => setLoading(false));
  };

  const handleTabChange = (key) => {
    const idx = Number(key);
    setActiveBin(idx);
    setPage(1); // 切换tab时重置到第一页
    loadBinDataBatch(idx, 1, pageSize);
  };

  const controlSize = "middle";

  const pagedSampleData = mode === 'sample'
    ? data.slice((samplePage - 1) * samplePageSize, samplePage * samplePageSize)
    : data;

  const currentBinData = binData[activeBin]?.data || [];
  const tableData = mode === 'sample'
    ? pagedSampleData
    : mode === 'abnormal'
      ? data
      : currentBinData.slice((page - 1) * pageSize, page * pageSize);

  const pagination = mode === 'all' ? {
    current: page,
    pageSize,
    total: abnormalMode ? total : (binData[activeBin]?.total ?? total),
    showTotal: (total) => `共 ${total} 条`,
    showSizeChanger: true,
    onChange: handleTableChange,
  } : mode === 'sample' ? {
    current: samplePage,
    pageSize: samplePageSize,
    total: data.length,
    showTotal: (total) => `共 ${total} 条`,
    showSizeChanger: true,
    onChange: handleSampleTableChange,
  } : false;

  const showTabs = mode !== 'abnormal';

  // 价格区间详细文案
  const binLabels = [
    "0-10 USD",
    "10-100 USD",
    "100-1K USD",
    "1K-10K USD",
    "10K-100K USD",
    "100K+ USD"
  ];

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ background: "#fff", padding: "10px 24px 10px 24px", height: 'auto', display: 'flex', alignItems: 'center' }}>
        <Row align="middle" justify="space-between" style={{ width: '100%' }}>
          <Col flex="none">
            <Title level={4} style={{ margin: 0, display: 'flex', alignItems: 'center', height: '100%' }}>Price Checker</Title>
          </Col>
          <Col flex="auto">
            <Row gutter={[8, 8]} align="middle" justify="end" wrap style={{ height: '100%' }}>
              <Col>
                <Spin spinning={tokenLoading} size={controlSize}>
                  <Select
                    allowClear
                    showSearch
                    style={{ minWidth: 130 }}
                    placeholder="Token"
                    value={selectedToken}
                    onChange={setSelectedToken}
                    optionFilterProp="children"
                    filterOption={(input, option) =>
                      (option?.children ?? '').toLowerCase().includes(input.toLowerCase())
                    }
                    size={controlSize}
                  >
                    {topTokens.map(t => (
                      <Option key={t.token_mint_address} value={t.token_mint_address}>
                        ({t.count}) {t.token_mint_address.slice(0, 6)}...
                      </Option>
                    ))}
                  </Select>
                </Spin>
              </Col>
              <Col>
                <Select
                  value={priceType}
                  onChange={setPriceType}
                  style={{ minWidth: 70 }}
                  size={controlSize}
                >
                  <Option value="buy_price">买价</Option>
                  <Option value="sell_price">卖价</Option>
                </Select>
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>Rows</span>
                <InputNumber min={1} max={10000} value={rows} onChange={setRows} style={{ width: 90 }} size={controlSize} />
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>Tokens</span>
                <InputNumber min={1} max={1000} value={tokens} onChange={setTokens} style={{ width: 90 }} size={controlSize} />
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>SOL/USD</span>
                <InputNumber min={1} max={10000} value={solPrice} onChange={setSolPrice} style={{ width: 100 }} size={controlSize} />
              </Col>
              <Col>
                <Select
                  value={priceUnit}
                  onChange={setPriceUnit}
                  style={{ minWidth: 70 }}
                  size={controlSize}
                >
                  <Option value="SOL">SOL</Option>
                  <Option value="USD">USD</Option>
                </Select>
              </Col>
              <Col>
                <Select
                  value={timezone}
                  onChange={setTimezone}
                  style={{ minWidth: 80 }}
                  size={controlSize}
                >
                  <Option value="gmt0">GMT+0</Option>
                  <Option value="gmt8">GMT+8</Option>
                </Select>
              </Col>
              <Col>
                <Select
                  value={showFullToken}
                  onChange={setShowFullToken}
                  style={{ minWidth: 90 }}
                  size={controlSize}
                >
                  <Option value={false}>Token缩略</Option>
                  <Option value={true}>Token全显</Option>
                </Select>
              </Col>
              <Col>
                <Button type="primary" onClick={handleQuery} loading={loading} size={controlSize}>
                  查询
                </Button>
              </Col>
              <Col>
                <AbnormalFilterButton
                  loading={loading}
                  abnormalMode={mode === 'abnormal'}
                  onClick={handleAbnormalFilter}
                />
              </Col>
              <Col>
                <Button onClick={handleReset} size={controlSize}>重置</Button>
              </Col>
            </Row>
          </Col>
        </Row>
      </Header>
      <Content style={{ padding: 24 }}>
        {showTabs && (
          <Tabs
            activeKey={activeBin.toString()}
            onChange={handleTabChange}
            items={binIdxList.map(idx => ({
              key: idx.toString(),
              label: binLabels[idx] || `Bin${idx}`
            }))}
          />
        )}
        <PriceTable
          data={tableData}
          loading={mode === 'sample' ? loading : binLoading || loading}
          timezone={timezone}
          showFullToken={showFullToken}
          priceUnit={priceUnit}
          pagination={pagination}
          summary={mode === 'sample' ? summary : summary}
        />
      </Content>
    </Layout>
  );
}

export default App;
