import React, { useState, useEffect } from "react";
import { Layout, InputNumber, Button, Row, Col, Typography, message, Select, Spin, Tabs } from "antd";
import PriceTable from "./components/PriceTable";
import { fetchRandomSample, fetchTopTokens, fetchPriceRanges, fetchFilterData, fetchBatchBinsData } from "./api";

const { Header, Content } = Layout;
const { Title } = Typography;
const { Option } = Select;
const { TabPane } = Tabs;

function App() {
  const [rows, setRows] = useState(100);
  const [tokens, setTokens] = useState(10);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filteredData, setFilteredData] = useState([]);
  const [topTokens, setTopTokens] = useState([]);
  const [tokenLoading, setTokenLoading] = useState(false);
  const [selectedToken, setSelectedToken] = useState(null);
  const [priceType, setPriceType] = useState("buy_price");
  const [priceRanges, setPriceRanges] = useState({ low: null, mid: null, high: null, bins: [] });
  const [rangeLoading, setRangeLoading] = useState(false);
  const [selectedRange, setSelectedRange] = useState(null);
  const [priceUnit, setPriceUnit] = useState("USD"); 
  const [timezone, setTimezone] = useState("gmt8");
  const [showFullToken, setShowFullToken] = useState(false);
  const [solPrice, setSolPrice] = useState(133); 
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);
  const [total, setTotal] = useState(0);
  const [mode, setMode] = useState('all'); // all: 全量分页, sample: 随机抽样

  // 区间tab相关
  const [activeBin, setActiveBin] = useState(4); // 默认100-1K区间
  const [binData, setBinData] = useState({}); // {binIdx: {data, total}}
  const [binLoading, setBinLoading] = useState(false);
  const binIdxList = [4, 3, 2, 5, 1, 0]; // 价格从高到低，但默认加载顺序调整

  useEffect(() => {
    setTokenLoading(true);
    fetchTopTokens(20)
      .then(setTopTokens)
      .catch(err => console.error("Fetch top tokens error:", err))
      .finally(() => setTokenLoading(false));
  }, []);

  useEffect(() => {
    setRangeLoading(true);
    fetchPriceRanges(priceType, priceUnit, solPrice)
      .then(setPriceRanges)
      .catch(err => console.error("Fetch price ranges error:", err))
      .finally(() => setRangeLoading(false));
  }, [priceType, priceUnit, solPrice]);

  useEffect(() => {
    let intermediateData = data;
    if (selectedToken) {
      intermediateData = intermediateData.filter(row => row.token_mint_address === selectedToken);
    }
    if (selectedRange !== null && priceRanges.bins.length > 0) {
      intermediateData = intermediateData.filter(row => {
        let price = priceType === "buy_price" ? row.buy_price : row.sell_price;
        if (priceUnit === "USD" && row.price_unit === "SOL") {
          price = price * solPrice;
        }
        if (priceUnit === "SOL" && row.price_unit === "USD") {
          price = price / solPrice;
        }
        const bin = priceRanges.bins[selectedRange];
        return price >= bin.low && price <= bin.high;
      });
    }
    setFilteredData(intermediateData);
  }, [data, selectedToken, selectedRange, priceType, priceUnit, priceRanges, solPrice]);

  const fetchPageData = (params = {}) => {
    setLoading(true);
    fetchFilterData({
      priceType,
      priceUnit,
      solPrice,
      token: selectedToken,
      priceBin: selectedRange,
      page,
      pageSize,
      ...params
    })
      .then(res => {
        setData(res.data || []);
        setTotal(res.total || 0);
        setMode('all');
      })
      .catch(err => {
        message.error('获取数据失败');
        setData([]);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    setPage(1);
    fetchPageData({ page: 1 });
  }, [selectedToken, selectedRange, priceType, priceUnit, solPrice]);

  const handleTableChange = (pagination) => {
    setPage(pagination.current);
    setPageSize(pagination.pageSize);
    fetchPageData({ page: pagination.current, pageSize: pagination.pageSize });
  };

  const handleQuery = () => {
    setLoading(true);
    fetchRandomSample(rows, tokens, { priceType, priceUnit, solPrice, token: selectedToken, priceBin: selectedRange })
      .then(res => {
        setData(res.data || []);
        setTotal(res.data?.length || 0);
        setMode('sample');
        message.success(`成功获取 ${res.data?.length || 0} 条随机样本`);
      })
      .catch(err => {
        message.error('获取数据失败');
        setData([]);
      })
      .finally(() => setLoading(false));
  };

  // 批量加载高价区间（默认加载100-1K、1K-10K、10K-100K）
  useEffect(() => {
    setBinLoading(true);
    fetchBatchBinsData({
      priceType,
      priceUnit,
      solPrice,
      bins: [4, 3, 2] // 默认加载100-1K、1K-10K、10K-100K
    })
      .then(res => {
        setBinData(res);
        setActiveBin(4); // 默认激活100-1K区间
      })
      .finally(() => setBinLoading(false));
  }, [priceType, priceUnit, solPrice]);

  // 切换tab时异步加载其它区间
  const handleTabChange = (key) => {
    const idx = Number(key);
    setActiveBin(idx);
    if (!binData[idx]) {
      setBinLoading(true);
      fetchBatchBinsData({
        priceType,
        priceUnit,
        solPrice,
        bins: [idx]
      })
        .then(res => setBinData(prev => ({ ...prev, ...res })))
        .finally(() => setBinLoading(false));
    }
  };

  // Tabs渲染
  const binTabs = binIdxList.map(idx => {
    const bin = priceRanges.bins[idx] || {};
    const label = bin.label || `Bin${idx}`;
    const rangeStr = bin.low !== undefined ? `${bin.low}~${bin.high ?? '∞'}` : '';
    return {
      key: idx.toString(),
      label: `${label} [${rangeStr}] USD`,
      children: (
        <PriceTable
          data={binData[idx]?.data || []}
          loading={binLoading && activeBin === idx}
          pagination={{ current: 1, pageSize: 20, total: binData[idx]?.total || 0 }}
        />
      )
    }
  });

  const controlSize = "middle";

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ background: "#fff", padding: "10px 24px 10px 24px", height: 'auto', display: 'flex', alignItems: 'center' }}>
        <Row align="middle" justify="space-between" style={{ width: '100%' }}>
          <Col flex="none">
            <Title level={4} style={{ margin: 0, display: 'flex', alignItems: 'center', height: '100%' }}>Price Checker</Title>
          </Col>
          <Col flex="auto">
            <Row gutter={[8, 8]} align="middle" justify="end" wrap style={{ height: '100%' }}>
              <Col>
                <Spin spinning={tokenLoading} size={controlSize}>
                  <Select
                    allowClear
                    showSearch
                    style={{ minWidth: 130 }}
                    placeholder="Token"
                    value={selectedToken}
                    onChange={setSelectedToken}
                    optionFilterProp="children"
                    filterOption={(input, option) =>
                      (option?.children ?? '').toLowerCase().includes(input.toLowerCase())
                    }
                    size={controlSize}
                  >
                    {topTokens.map(t => (
                      <Option key={t.token_mint_address} value={t.token_mint_address}>
                        ({t.count}) {t.token_mint_address.slice(0, 6)}...
                      </Option>
                    ))}
                  </Select>
                </Spin>
              </Col>
              <Col>
                <Select
                  value={priceType}
                  onChange={setPriceType}
                  style={{ minWidth: 70 }}
                  size={controlSize}
                >
                  <Option value="buy_price">买价</Option>
                  <Option value="sell_price">卖价</Option>
                </Select>
              </Col>
              <Col>
                <Spin spinning={rangeLoading} size={controlSize}>
                  <Select
                    allowClear
                    style={{ minWidth: 120 }}
                    placeholder={`区间(${priceUnit})`}
                    value={selectedRange}
                    onChange={setSelectedRange}
                    size={controlSize}
                  >
                    {Array.isArray(priceRanges.bins)
                      ? priceRanges.bins.map((bin, idx) => {
                          let low = Number(bin.low);
                          let high = bin.high !== null ? Number(bin.high) : '∞';
                          let unit = priceRanges.unit || 'USD';
                          // 格式化区间
                          const rangeStr = `${low} ~ ${high}`;
                          const percentStr = `(${bin.count} rows, ${bin.percent}%)`;
                          return (
                            <Option key={idx} value={idx}>
                              <span title={`${bin.label} [${rangeStr}] ${unit} ${percentStr}`}>
                                {bin.label} [{rangeStr}] {unit} {percentStr}
                              </span>
                            </Option>
                          );
                        })
                      : (
                        <Option value={null} disabled>
                          暂无区间
                        </Option>
                      )}
                  </Select>
                </Spin>
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>Rows</span>
                <InputNumber min={1} max={10000} value={rows} onChange={setRows} style={{ width: 90 }} size={controlSize} />
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>Tokens</span>
                <InputNumber min={1} max={1000} value={tokens} onChange={setTokens} style={{ width: 90 }} size={controlSize} />
              </Col>
              <Col>
                <span style={{ marginRight: 4 }}>SOL/USD</span>
                <InputNumber min={1} max={10000} value={solPrice} onChange={setSolPrice} style={{ width: 100 }} size={controlSize} />
              </Col>
              <Col>
                <Select
                  value={priceUnit}
                  onChange={val => { setPriceUnit(val); setSelectedRange(null); }}
                  style={{ minWidth: 70 }}
                  size={controlSize}
                >
                  <Option value="SOL">SOL</Option>
                  <Option value="USD">USD</Option>
                </Select>
              </Col>
              <Col>
                <Select
                  value={timezone}
                  onChange={setTimezone}
                  style={{ minWidth: 80 }}
                  size={controlSize}
                >
                  <Option value="gmt0">GMT+0</Option>
                  <Option value="gmt8">GMT+8</Option>
                </Select>
              </Col>
              <Col>
                <Select
                  value={showFullToken}
                  onChange={setShowFullToken}
                  style={{ minWidth: 90 }}
                  size={controlSize}
                >
                  <Option value={false}>Token缩略</Option>
                  <Option value={true}>Token全显</Option>
                </Select>
              </Col>
              <Col>
                <Button type="primary" onClick={handleQuery} loading={loading} size={controlSize}>
                  查询
                </Button>
              </Col>
            </Row>
          </Col>
        </Row>
      </Header>
      <Content style={{ padding: 24 }}>
        <Tabs
          items={binTabs}
          activeKey={activeBin.toString()}
          onChange={handleTabChange}
        />
      </Content>
    </Layout>
  );
}

export default App;
